	sort.Sort(expectedFields)
	bytez, err = json.Marshal(expectedFields)
	assert.Nil(err)
	t.Log(string(bytez))
	t.Log(fieldsOutput5_1_1)
	assert.Equal(expectedFields, fields)
	//assert.NotEqual(Fields{Field{Name: "Tom"}}, fields)
